-- ----------------------------
-- Table structure for game
-- ----------------------------
DROP TABLE IF EXISTS `game`;
CREATE TABLE `game` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `name` varchar(32) NOT NULL,
  `translated_name` varchar(32) NOT NULL,
  `cover_image` varchar(1024) DEFAULT NULL,
  `pitctures` varchar(10250) DEFAULT NULL,
  `platform` varchar(110) DEFAULT NULL,
  `release_date` date DEFAULT NULL,
  `tag` varchar(1100) DEFAULT NULL,
  `official_website` varchar(1024) DEFAULT NULL,
  `developer` varchar(32) DEFAULT NULL,
  `publisher` varchar(32) DEFAULT NULL,
  `description` varchar(1000) DEFAULT NULL,
  `number_of_judges` bigint NOT NULL,
  `total_score` bigint DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of game
-- ----------------------------

-- ----------------------------
-- Table structure for game_topic_related
-- ----------------------------
DROP TABLE IF EXISTS `game_topic_related`;
CREATE TABLE `game_topic_related` (
  `game_id` bigint NOT NULL,
  `topic_id` bigint NOT NULL,
  PRIMARY KEY (`game_id`,`topic_id`),
  KEY `FK_game_topic_related2` (`topic_id`),
  CONSTRAINT `FK_game_topic_related` FOREIGN KEY (`game_id`) REFERENCES `game` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `FK_game_topic_related2` FOREIGN KEY (`topic_id`) REFERENCES `topic` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of game_topic_related
-- ----------------------------

-- ----------------------------
-- Table structure for topic
-- ----------------------------
DROP TABLE IF EXISTS `topic`;
CREATE TABLE `topic` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `editor_id` bigint NOT NULL,
  `author_id` bigint NOT NULL,
  `content` varchar(20000) NOT NULL,
  `type` char(4) NOT NULL,
  `title` varchar(50) NOT NULL,
  `resume` varchar(50) NOT NULL,
  `status` varchar(10) NOT NULL,
  `modify_time` datetime NOT NULL,
  `change_time` datetime NOT NULL,
  `cover_image` varchar(1024) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_edit` (`editor_id`),
  KEY `FK_write` (`author_id`),
  CONSTRAINT `FK_edit` FOREIGN KEY (`editor_id`) REFERENCES `user` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `FK_write` FOREIGN KEY (`author_id`) REFERENCES `user` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE=InnoDB AUTO_INCREMENT=1083976 DEFAULT CHARSET=utf8;

